	public static void encryption(String company, int start, long end) throws Exception {

		File clearText = new File("D:/QRcode/clearText.txt");
		File cipherText = new File("D:/QRcode/cipherText.txt");
		File logText = new File("D:/QRcode/log/logText.txt");

		FileOutputStream clearTextFos = new FileOutputStream(clearText);
		FileOutputStream cipherTextFos = new FileOutputStream(cipherText);
		FileOutputStream logTextFos = new FileOutputStream(logText);

		CountingOutputStream clearTextCountStream = new CountingOutputStream(clearTextFos);
		CountingOutputStream cipherTextCountStream = new CountingOutputStream(cipherTextFos);
		CountingOutputStream logTextCountStream = new CountingOutputStream(logTextFos);

		String cSrc = "";

		try {

			for (int i = start; i <= end; i++) {

				cSrc = company + getCode(i);
				System.out.println("加密前的字串是：" + cSrc);

				clearTextCountStream.write(cSrc.getBytes());
				clearTextCountStream.write("\r\n".getBytes());

				logTextCountStream.write(cSrc.getBytes());
				logTextCountStream.write("   ".getBytes());

				// 加密
				String enString = AES.Encrypt(cSrc, cKey);
				System.out.println("加密后的字串是：" + enString);
				enString = url + URLEncoder.encode(enString, "utf-8");
				System.out.println("最终的字串是：" + enString);

				cipherTextCountStream.write(enString.getBytes());
				cipherTextCountStream.write("\r\n".getBytes());

				logTextCountStream.write(enString.getBytes());
				logTextCountStream.write("\r\n".getBytes());
			}
		} finally {
			IOUtils.closeQuietly(clearTextCountStream);
			IOUtils.closeQuietly(cipherTextCountStream);
		}
	}

	public static void decrypt() throws Exception {

		File decryptionText = new File("D:/QRcode/decryptionText.txt");
		File cipherText = new File("D:/QRcode/cipherText.txt");

		FileOutputStream decryptionTextFos = new FileOutputStream(decryptionText);

		CountingOutputStream decryptionTextCountStream = new CountingOutputStream(decryptionTextFos);

		String enText = FileUtils.readFileToString(cipherText);

		String enArray[] = enText.split("\r\n");

		try {
			for (String enString : enArray) {

				// 解密
				String DeString = AirBottleCodeInitUtils.initCode(enString);
				System.out.println("解密后的字串是：" + DeString);

				decryptionTextCountStream.write(DeString.getBytes());
				decryptionTextCountStream.write("\r\n".getBytes());

			}
		} finally {
			IOUtils.closeQuietly(decryptionTextCountStream);

		}

	}

	public static String getCode(long intHao) {

		String STR_FORMAT = "00000000";

		DecimalFormat df = new DecimalFormat(STR_FORMAT);

		return df.format(intHao);

	}

	public static String decrypt(String code) {

		// 解密
		String DeString = null;
		try {
			DeString = AES.Decrypt(code, cKey);
		} catch (Exception e) {
			log.error("解密出错:" + DeString);
			log.error(e);
		}

		return DeString;
	}
